class CustomInput extends Component{
    constructor(props){
        super(props);
        this.state = {
value:"Input Pertama"
        };
    }
render(){
    return(
        <div>
            <input type ="text"  value={this.state.value}/>
            <button>Kirim</button>
        </div>
    )
}
}

export default CustomInput;
